<?php

namespace app\modules\apv1\models;

use yii\data\ActiveDataProvider;

class NotassalidaSearch extends \app\models\Notassalida
{
    public $categoria;
    public $empresa;


    public function fields()
    {
        return parent::fields(); // TODO: Change the autogenerated stub
    }
//    public function fields()
//    {
//        return ['id','title','comments']; // TODO: Change the autogenerated stub
//    }

    /**
     * {@inheritdoc}
     */
    public function rules()
    {
        return [
            [['id'], 'integer'],
            [['id','fec_emision','fec_notificado','categoria','empresa','detalle','notificado'], 'safe'],
        ];
    }

    /**
     * {@inheritdoc}
     */
//    public function scenarios()
//    {
//        // bypass scenarios() implementation in the parent class
//        return Model::scenarios();
//    }

    /**
     * Creates data provider instance with search query applied
     *
     * @param array $params
     *
     * @return ActiveDataProvider
     */
    public function search($params)
    {
        $query = Notassalida::find();

        $query->joinWith('categoria');
        $query->joinWith('empresa');


        $dataProvider = new ActiveDataProvider([
            'query' => $query,
        ]);

        $this->load($params,'');

        if (!$this->validate()) {
            // uncomment the following line if you do not want to return any records when validation fails
            // $query->where('0=1');
           return $dataProvider;
       }
        // grid filtering conditions
        $query->andFilterWhere([
            'id' => $this->id,
        ]);
        $query->andFilterWhere(['like', 'fec_emision', $this->fec_emision])
            ->andFilterWhere(['like', 'fec_notificado', $this->fec_notificado]);
        $query->andFilterWhere(['like','categoria.nombre',$this->categoria]);
        return $dataProvider;
    }
}